import 'package:flutter/material.dart';class AuthButton extends StatelessWidget {  final onPressed;  final title;  const AuthButton({    Key? key,required  this.title, this.    onPressed,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return ElevatedButton(      style: ElevatedButton.styleFrom(        backgroundColor: Colors.blueAccent,        minimumSize:const Size(double.infinity, 50),        shape: RoundedRectangleBorder(          borderRadius: BorderRadius.circular(7.5),          side: BorderSide(color: Colors.white54.withOpacity(0.2), width: 2),        ),      ),      onPressed: onPressed,      child: Text(        title,        style: const TextStyle(          fontSize: 16,          color: Colors.white,        ),      ),    );  }}