import 'package:flutter/material.dart';class AuthTextField extends StatelessWidget {  final hintText;  final controller;  final isHidden;  const AuthTextField({    Key? key, this.hintText,this.isHidden,this.controller  }) : super(key: key);  @override  Widget build(BuildContext context) {    return ClipRRect(      borderRadius: BorderRadius.circular(7.5),      child: TextField(        style: const TextStyle(fontSize: 16, color: Colors.black),        controller: controller,        obscureText: isHidden ?? false,        decoration: InputDecoration(            focusedBorder:const OutlineInputBorder(),            border: const OutlineInputBorder(),            filled: true,            fillColor: Colors.white54.withOpacity(0.2),            hintText: hintText,            hintStyle: const TextStyle(color: Colors.grey)),      ),    );  }}