import 'package:instagram_clone/features/auth/domain/entities/user_entities.dart';class UserModel extends User{  UserModel({required super.fullName, required super.email, required super.password});  factory UserModel.fromJson(Map<String,dynamic> json){    var user  = UserModel(fullName: json['fullName'],password:    json['password'], email:    json['email']);    return user;  }  Map<String, dynamic> toJson() => {    "uid": uid,    "fullName": fullName,    "email": email,    "password": password,    "imageUrl": imageUrl,    "followingCount": followingCount,    "followersCount": followersCount,    'device_id': device_id,    'device_type': device_type,    'device_token': device_token,  };}