import 'package:instagram_clone/features/post/domain/entities/post_entities.dart';class PostModel extends Post{  PostModel({required super.postImage, required super.caption});factory PostModel.fromJson(Map<String,dynamic> json){  var post  = PostModel(postImage: json['postImage'], caption:  json['caption']);  return post;}  Map<String, dynamic> toJson() => {    "uid": uid,    "fullName": fullName,    "id": id,    "postImage": postImage,    "caption": caption,    "createdDate": createdDate,    "isLiked": isLiked,    "isMine": isMine,    "imageUser": imageUser,  };}